mapper.select:
select t1.id, t1.enterprise_name, t1.`type`, t1.nature, t1.set_up_date, t1.legal_person, t1.legal_person_id_card, t1.operation_status,
t1.reg_capital, t1.address, t1.cultivated_area, t1.work_person_num,t1.region,t1.dept_id
from tea_enterprise_info t1 LEFT JOIN sys_dept t2 ON t2.dept_id = t1.dept_id

    and (t2.dept_id = #{deptId} OR FIND_IN_SET(#{deptId},t2.ancestors))
     and t1.enterprise_name like concat('%', #{enterpriseName}, '%')
     and t1.type = #{type}
     and t1.nature = #{nature}
     and t1.set_up_date = #{setUpDate}
     and t1.legal_person = #{legalPerson}
     and t1.legal_person_id_card = #{legalPersonIdCard}
     and t1.operation_status = #{operationStatus}
     and t1.reg_capital = #{regCapital}
     and t1.address = #{address}
     and t1.cultivated_area = #{cultivatedArea}
     and t1.work_person_num = #{workPersonNum}
     and t1.region = #{region}
    and t1.is_county_emp = #{isCountyEmp}
    and t1.is_town_emp = #{isTownEmp}

ORDER BY `order`

SELECT t2.dict_label `name`, COUNT( t1.tea_type ) `value` FROM `tea_enterprise_tea_type` t1 INNER JOIN sys_dict_data t2 ON t2.dict_value = t1.tea_type AND t2.dict_type = 'sys_tea_type' INNER JOIN tea_enterprise_info t3 ON t3.id = t1.tea_enterprise_id
t3.region = #{region}
GROUP BY t1.tea_type ORDER BY t2.dict_sort

mapper.update:
update tea_enterprise_info
enterprise_name = #{enterpriseName}, type = #{type}, nature = #{nature}, set_up_date = #{setUpDate}, legal_person = #{legalPerson}, legal_person_id_card = #{legalPersonIdCard}, operation_status = #{operationStatus}, reg_capital = #{regCapital}, address = #{address}, cultivated_area = #{cultivatedArea}, work_person_num = #{workPersonNum}, region = #{region}, dept_id = #{deptId},
where id = #{id}
