mapper.select:
SELECT SUM(sales_volume_number) salesNumber ,SUM(sales_volume_money) salesMoney FROM tea_enterprise_yield_sales WHERE `year` BETWEEN #{start} AND #{end}
AND tea_peasant_id = #{enterpriseId}
GROUP BY `year` ORDER BY `year`

SELECT SUM(t1.product_cost_statistics) cost,SUM(t1.product_profit_statistics) profit,t1.`year` FROM tea_enterprise_yield_sales t1 INNER JOIN tea_enterprise_info t2 ON t2.id = t1.tea_peasant_id WHERE t1.`year` >= #{start} AND t1.`year` <= #{end}
AND t1.tea_peasant_id = #{enterpriseId}
GROUP BY t1.`year` ORDER BY t1.`year`

mapper.update:
update tea_enterprise_yield_sales
`year` = #{year}, yield_value_number = #{yieldValueNumber}, yield_value_money = #{yieldValueMoney}, sales_volume_number = #{salesVolumeNumber}, sales_volume_money = #{salesVolumeMoney}, product_profit_statistics = #{productProfitStatistics}, product_cost_statistics = #{productCostStatistics}, tea_peasant_id = #{teaPeasantId},
where id = #{id}
